const Main = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    canvas.width = window.innerWidth * 2;
  }, []);

  const startDraw = () => {};

  const endDraw = () => {};

  const draw = () => {};

  return (
    <canvas
      onMouseDown={startDraw}
      onMouseUp={endDraw}
      onMouseMove={draw}
      ref={canvasRef}
    />
  );
};

export default Main;
