import { Song, Track, Instrument, Effect } from 'reactronica';
import React, { useState } from 'react';
import * as Tone from 'tone';
import Button from 'react-bootstrap/Button';

const Controller = () => {
  const synthOne = new Tone.FMSynth().toDestination(),
    synthOneStartLooper = () => {
      const looper = new Tone.Loop(synthOneLooper, '8n');
      if (Tone.Transport.state === 'started') {
        looper.stop(0);
      }
      looper.start(0);
      console.log(TransportTime);
    },
    synthOneLooper = (time) => {
      synthOne.triggerAttackRelease('c3', '0.01', time);
      console.log();
    },
    transportStart = () => {
      Tone.Transport.scheduleRepeat(synthOneStartLooper, '8n');
      Tone.Transport.start();
      console.log(Tone.Transport.state);
    };

  return (
    <>
      <Button onClick={transportStart}>Start Transport</Button>
      <Button onClick={synthOneStartLooper}>Start Synth Looper</Button>
    </>
  );
};

export default Controller;
