import { Song, Track, Instrument, Effect } from 'reactronica';
import * as Tone from 'tone';

const Controller = () => {
  const voice1 = new Tone.Oscillator().toDestination();
  const voice2 = new Tone.AMSynth().toDestination();
  const voice3 = new Tone.FMSynth().toDestination();
  const voice4 = new Tone.MembraneSynth().toDestination();

  // const repeater = () => {
  //   Tone.Transport.scheduleRepeat((time) => {
  //     voice1.start(time).stop(time + 0.1);
  //   }, '4n');
  // };

  const transportStart = () => {
    Tone.Transport.start();
    const looper = new Tone.Loop(track, '4n');
    looper.start(0);
  };

  const transportStart2 = () => {
    // Tone.Transport.start();
    const looper = new Tone.Loop(track2, '8n');
    looper.start(0);
  };

  const transportStart3 = () => {
    // Tone.Transport.start();
    const looper = new Tone.Loop(track3, '16n');
    looper.start(0);
  };

  const track = (time) => {
    voice2.triggerAttackRelease('c1', '8n', time);
    console.log(time);
  };

  const track2 = (time) => {
    voice3.triggerAttackRelease('c3', '8n', time);
    console.log(time);
  };

  const track3 = (time) => {
    voice4.triggerAttackRelease('c5', '8n', time);
    console.log(time);
  };

  const transportStop = () => {
    Tone.Transport.stop();
  };

  return (
    <>
      {/* <button onClick={repeater}>start</button> */}
      <button onClick={transportStart}>track 1</button>
      <button onClick={transportStart2}>track 2</button>
      <button onClick={transportStart3}>track 3</button>
      <button onClick={transportStop}>stahp</button>
    </>
  );
};

export default Controller;
