import React, { useState } from 'react';

const Form = () => {
  const [preset, setPreset] = useState({
    reverb: '',
    delay: '',
    tremolo: '',
    waveshape: '',
    envelope: '',
  });

  const presetChange = (e) =>
    setPreset({
      ...preset,
      [e.currentTarget.name]: e.currentTarget.value,
    });

  const handleSubmit = (e) => {
    e.preventDefault();

    console.log(preset);
    alert(`Submitting Preset!`);
  };

  return (
    <div>
      <h3>Create Synthesizer Preset</h3>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="reverb">Reverb</label>
          <input
            type="text"
            id="reverb"
            value={preset.reverb}
            onChange={(e) => setPreset(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="delay">Delay</label>
          <input
            type="text"
            id="delay"
            value={preset.delay}
            onChange={(e) => setPreset(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="tremolo">Tremolo</label>
          <input
            type="text"
            id="tremolo"
            value={preset.tremolo}
            onChange={(e) => setPreset(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="waveshape">Waveshape</label>
          <input
            type="text"
            id="waveshape"
            value={preset.waveshape}
            onChange={(e) => setPreset(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="envelope">Envelope</label>
          <input
            type="text"
            id="envelope"
            value={preset.envelope}
            onChange={(e) => setPreset(e.target.value)}
          />
        </div>
        <input type="submit" value="Save Preset" />
      </form>
    </div>
  );
};

export default Form;
