import { Song, Track, Instrument, Effect } from 'reactronica';
import * as Tone from 'tone';
import Button from 'react-bootstrap/Button';

const Controller = () => {
  const voice1 = new Tone.Oscillator().toDestination();
  const loopVoice1 = () => {
    Tone.Transport.start();
    new Tone.Loop(loopVoice1Callback, '4n');
  };
  const loopVoice1Callback = (time) => {
    voice1.triggerAttackRelease('C3', '8n', time);
  };
  return (
    <>
      <Button OnClick={loopVoice1}>Start</Button>
    </>
  );
};

export default Controller;
