import React, { useEffect, useRef, useState } from 'react';

const Main = () => {
  // ref objects can be used to store references for elements and any info that needs to be preserved between re-renders
  const canvasRef = useRef(null);
  const contextRef = useRef(null);
  const [currentlyDrawing, setCurrentlyDrawing] = useState(false);

  useEffect(() => {
    const canvas = canvasRef.current;

    // optimize for bigger displays
    canvas.width = window.innerWidth * 2;
    canvas.height = window.innerHeight * 2;
    canvas.style.width = `${window.innerWidth}px`;
    canvas.style.height = `${window.innerHeight}px`;

    // define 2d context to enable drawing
    const context = canvas.getContext('2d');

    // define brush settings and canvas size
    context.scale(2, 2);
    context.lineCap = 'round';
    context.strokeStle = 'red';
    context.lineWidth = 6;
    contextRef.current = context;
  }, []);

  const startDraw = ({ nativeEvent }) => {
    const { offsetX, offsetY } = nativeEvent;
    contextRef.current.beginPath();
    contextRef.current.moveTo(offsetX, offsetY);
    setCurrentlyDrawing(true);
  };

  const endDraw = () => {
    contextRef.current.closePath();
    setCurrentlyDrawing(false);
  };

  const draw = ({ nativeEvent }) => {
    if (!currentlyDrawing) {
      return;
    }
    const { offsetX, offsetY } = nativeEvent;
    contextRef.current.lineTo(offsetX, offsetY);
    contextRef.current.stroke();
  };

  const saveCanvas = () => {
    let savedCanvas = document.getElementById('canvas');
    let canvasUrl = savedCanvas.toDataURL();
  };

  return (
    <>
      <canvas
        id="canvas"
        onMouseDown={startDraw}
        onMouseUp={endDraw}
        onMouseMove={draw}
        ref={canvasRef}
      />
      <button onClick={saveCanvas}>save drawing</button>
    </>
  );
};

export default Main;
