import { Song, Track, Instrument, Effect } from 'reactronica';
import * as Tone from 'tone';

const Controller = () => {
  const voice1 = new Tone.Synth().toDestination();
  const voice2 = new Tone.Synth().toDestination();
  const voice3 = new Tone.Synth().toDestination();
  const voice4 = new Tone.Synth().toDestination();

  const loop1 = new Tone.Loop((time) => {
    voice1.triggerAttackRelease('C2', '8n', time);
  }, '4n').start(0);

  const loop2 = new Tone.Loop((time) => {
    voice2.triggerAttackRelease('C4', '8n', time);
  }, '4n').start('8n');

  const loop3 = new Tone.Loop((time) => {
    voice3.triggerAttackRelease('C6', '8n', time);
  }, '4n').start('16n');

  const loop4 = new Tone.Loop((time) => {
    voice4.triggerAttackRelease('C8', '8n', time);
  }, '4n').start('32n');

  const transportStart = Tone.Transport.start;
  return (
    <div>
      <button onClick={transportStart()}>START</button>
    </div>
  );
};

export default Controller;
